import { json, redirect } from "react-router-dom";
import AuthForm from "../components/AuthForm";

function AuthenticationPage() {
  return <AuthForm />;
}

export default AuthenticationPage;
export const auth = ({ request }) => {
  const authSearch = new URL(request.url).searchParams;
  const mode = authSearch.get("mode") || "Login";
  if (mode !== "login" && mode !== "SignUp") {
    throw json({ message: "go to the reate page " }, { status: 422 });
  }
  const data = request.formdata();
  const authData = {
    email: data.get("email"),
    password: data.get("password"),
  };
  const response = fetch("http://loacalhost:8080" + mode, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(authData),
  });
  if (response.status === 422 || response.status === 401) {
    // throw json({ message: "error" }, { status: 422 });
    return response;
  }
  if (!response.ok) {
    throw json({ message: "there error" }, { status: 500 });
  }
  return redirect("/");
};
